	private static <T extends Block> RegistryObject<T> registerItemlessBlock(String registryName, Supplier<T> supplier) {
		return AoARegistries.BLOCKS.register(registryName, supplier);
	}

	private static <T extends Block> RegistryObject<T> registerBlock(String registryName, Supplier<T> supplier, @Nullable Supplier<CreativeModeTab>... creativeTabs) {
		return registerBlock(registryName, supplier, Rarity.COMMON, -1, creativeTabs);
	}

	private static <T extends Block> RegistryObject<T> registerBlock(String registryName, Supplier<T> supplier, int furnaceBurnTime, @Nullable Supplier<CreativeModeTab>... creativeTabs) {
		return registerBlock(registryName, supplier, Rarity.COMMON, furnaceBurnTime, creativeTabs);
	}

	private static <T extends Block> RegistryObject<T> registerBlock(String registryName, Supplier<T> supplier, Rarity rarity, @Nullable Supplier<CreativeModeTab>... creativeTabs) {
		return registerBlock(registryName, supplier, rarity, -1, creativeTabs);
	}

	private static <T extends Block> RegistryObject<T> registerBlock(String registryName, Supplier<T> supplier, Rarity rarity, int furnaceBurnTime, @Nullable Supplier<CreativeModeTab>... creativeTabs) {
		RegistryObject<T> block = AoARegistries.BLOCKS.register(registryName, supplier);
		
		AoAItems.registerItem(registryName, furnaceBurnTime > 0 ?
				() -> new BlockItem(block.get(), new Item.Properties().rarity(rarity)) {
					@Override
					public int getBurnTime(ItemStack itemStack, @Nullable RecipeType<?> recipeType) {
						return furnaceBurnTime;
					}} :
				() -> new BlockItem(block.get(), new Item.Properties().rarity(rarity)), creativeTabs);
		
		return block;
	}

	private static Block.Properties properties(Material material, MaterialColor mapColour) {
		return Block.Properties.of(material, mapColour);
	}

	@OnlyIn(Dist.CLIENT)
	public static void applyFluidRenderTypes() {
		for (RegistryObject<Block> block : AoARegistries.BLOCKS.getAllAoARegisteredObjects()) {
			Block bl = block.get();

			if (bl instanceof LiquidBlock liquid) {
				ItemBlockRenderTypes.setRenderLayer(((LiquidBlock)bl).getFluid().getFlowing(), RenderType.translucent());
				ItemBlockRenderTypes.setRenderLayer(((LiquidBlock)bl).getFluid().getSource(), RenderType.translucent());
			}
		}
	}

	public enum CustomRenderType {
		CUTOUT,
		CUTOUT_MIPPED,
		TRANSLUCENT
	}
